package com.xworkz.basics;

public class IntegersInstances {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
Integer inte=new Integer(14);
byte bs=inte.byteValue();
System.out.println(bs);
int n=inte.compareTo(8);
System.out.println(n);
double db=inte.doubleValue();
System.out.println(db);
boolean bl=inte.equals(1);
System.out.println(bl);
boolean bl1=inte.equals(14);
System.out.println(bl1);
float fl=inte.floatValue();
System.out.println(fl);
int in=inte.hashCode();
System.out.println(in);
int i=inte.intValue();
System.out.println(i);
long lg=inte.longValue();
System.out.println(lg);
short st=inte.shortValue();
System.out.println(st);
String s=inte.toString();
System.out.println(s);

	}

}
